    function init_video() {
        var is_mobile = false;
        var is_complete = $("ul.playlist li.with-video-play.playing").data('complete');
        var player = videojs('hocmaiplayer', {
            controls: true,
            preload: "auto",
            // autoplay: true,
            playbackRates: [0.5, 1.0, 1.5, 2.0],
            html5: {
                hlsjsConfig: {
                    debug: false,
                    maxBufferLength: 30,
                    maxMaxBufferLength: 30,
                    maxBufferSize: 30 * 1000 * 1000
                },
                hls: {
                    withCredentials: true
                }
            }
        });

        player.ready(function () {
            var player = this;
            window.player = player;

            if (!is_mobile) {
                player.seekButtons({
                    forward: 10,
                    back: 10
                });

                player.videoJsResolutionSwitcher({
                    default: 'low',
                    dynamicLabel: true
                });
            }

            player.playlist($html5Playlist);
            player.playlist.autoadvance(0);

            player.on("play", function () {
                var player = this;
                var $index = player.playlist.currentItem();
                $(player.posterImage.el()).hide();
                if ($index == -1) {
                    $index = 0;
                }
                playStatus($index, 'playing');
            });

            player.on("pause", function () {
                var player = this;
                var $index = player.playlist.currentItem();
                if ($index == -1) {
                    $index = 0;
                }
                playStatus($index, 'playing');
            });

            player.on('ended', function () {
                if(player.playlist.currentItem() + 1 == player.playlist().length) {
                    $(player.posterImage.el()).show();
                }
            });

            var totalVideo = $("ul.playlist li.with-video-play").length;
            player.on("timeupdate", function () {
                var player = this;
                var currentTime = player.currentTime();
                var duration = player.duration();
                if (currentTime / duration >= 0.05 && !is_complete){
                    var id = $("ul.playlist li.with-video-play.playing").data('id');
                    var uid = $("ul.playlist li.with-video-play.playing").data('uid');
                    var cmid = $("ul.playlist li.with-video-play.playing").data('cmid');
                    var vid = $("ul.playlist li.with-video-play.playing").data('vid');
                    if (typeof id !== 'undefined' && typeof uid !== 'undefined' && typeof cmid !== 'undefined' && typeof vid !== 'undefined'){
                        $.get("/local/omo_management/video/ajax/completion.php?id=" +id+ "&uid=" +uid+ "&cmid=" +cmid+ "&vid=" +vid, function(data, status){
                            if (status == "success" && data == "done"){
                                is_complete = 1;
                                $("ul.playlist li.with-video-play.playing").data('complete', is_complete);
                            }
                        });
                    }
                }
            });

            // player.muted(true);
            // var promise = player.play();

            setTimeout(function(){
                var promise = document.querySelector('#hocmaiplayer video').play();

                if (promise !== undefined) {
                    promise.then(function() {
                        // Autoplay started!
                        player.play();
                        console.log("Autoplay started");
                    }).catch(function(error) {
                        player.muted(true);
                        var $unmute = $(player.el_).parent().children('[data-toggle="unmute"]');
                        $unmute.show().data('player', player);
                        player.play();

                        // Autoplay was prevented.
                        console.log("Autoplay was prevented");
                    });
                }
            }, 500);
        });

        function playStatus(index, status) {
            console.log(index);
            clearCSS();
            var $clip = $("ul.playlist").find("li[position='" + (index) + "']");
            $($clip).addClass(status);
            if ($clip.parent().parent().is('li') && !$clip.parent().parent().attr("position")) {
                $clip.parent().parent().addClass(status);
            }
            is_complete = $("ul.playlist li.with-video-play.playing").data('complete');
        }

        function clearCSS() {
            $("ul.playlist li").removeClass("playing");
            $("ul.playlist li").removeClass("paused");
        }

        var c = 0;
        $("ul.playlist li").each(function () {
            if ($(this).find('ul>li').length == 0) {
                $(this).attr("position", c);
                if (c == 0)
                {
                    $(this).addClass("playing");
                    if ($(this).parent().parent().is('li') && !$(this).parent().parent().attr("position")) {
                        $(this).parent().parent().addClass("playing");
                    }
                }
                c++;
            }
        });

        $("ul.playlist li").bind('click', function (e) {
            if (e.target !== this || $(this).hasClass('playing')) {
                return;
            }

            var el = null;

            if ($(this).attr("position")) {
                el = $(this);
            } else {
                el = $($(this).find("li[position]")[0]);
            }

            var idx = parseInt(el.attr("position"));
            console.log('index');
            console.log(idx);

            if (el.hasClass('paused')) {
                player.resume();
            } else {
                player.playlist.currentItem(idx);
            }

            //player.getPlugin('menu').enableItems(true, parseInt(el.attr("position")));
        });

        //Playlist hover
        $('.struct-menu').hover(function(){
            var $this = $(this);
            if ($this.hasClass('on')) {
                var $playlist = $this.children('.playlist');
                $playlist.stop().show(300);
                $this.addClass('plshow');
            }
        }, function(){
            var $this = $(this);
            if ($this.hasClass('on')) {
                var $playlist = $this.children('.playlist');
                $playlist.stop().hide(150);
                $this.removeClass('plshow');
            }
        });
        $('.struct-menu').click(function(){
            var $this = $(this);
            if ($this.hasClass('on')) {
                var $playlist = $this.children('.playlist');
                if($this.hasClass('plshow')){
                    $playlist.stop().hide(150);
                    $this.removeClass('plshow');
                }else{
                    var $height = $('#hocmaiplayer').outerHeight();
                    if ($height) {
                        $playlist.css({maxHeight: $height});
                    }
                    $playlist.stop().show(300);
                    $this.addClass('plshow');
                }
            }
        });

        $('[data-toggle="unmute"]').click(function (e) {
            e.preventDefault();
            var $this = $(this),
                $player = $this.data('player');
            if (typeof $player == 'object') {
                $player.muted(false);
                $this.hide();
            }
        });
    }
