            function process_expand_folder(_this, isIconArrow, mode = 0, mode_course=0) {
                $('#id_mode_save').val('course_sections');
                $('.tree-items .tree-item-cover > .tree-item').removeClass('active');
                _this.closest('.tree-item').addClass('active');
                // Load child data
                var parent = _this.data('category-id');
                initSortable(".contain-category-" + parent);

                // Hidden path
                var hidden_path = _this.data('path');
                $('#form-folder-info input[name="hidden_path"]').val(hidden_path);

                var isRightArrow = false;
                if (isIconArrow == 1 && _this.attr('class').indexOf('fa-chevron-right') > -1) {
                    isRightArrow = true;
                }
                if (typeof isIconArrow == 'undefined' || isIconArrow == 0) {
                    isRightArrow = true;
                    _this = _this.closest('.left-item').find('.fa-chevron-right');
                }

                if (isRightArrow) {
                    _this.removeClass('fa-chevron-right');
                    _this.addClass('fa-chevron-down');
                    _this.attr('title', 'Thu g·ªçn');

                    get_course_sections(parent, mode, mode_course);
                } else {
                    // Get child
                    get_child_sections(parent);

                    // Get infomation activity by course
                    get_info_activity_by_course(parent);

                    _this.removeClass('fa-chevron-down');
                    _this.addClass('fa-chevron-right');
                    _this.attr('title', 'M·ªü r·ªông');
                    $('.above-block .contain-category-'+parent).html('');
                }
            }

